import { useEffect } from "react";
import { setProfile } from "../../app/store";
import "./User.css"
import TransactionSummary from "../../components/TransactionSummary/TransactionSummary"
import { useSelector, useDispatch } from "react-redux";

export default function User() {
    const token = useSelector(state => state.token)
    const firstName = useSelector(state => state.user.firstName)
    const lastName = useSelector(state => state.user.lastName)
    const userName = useSelector(state => state.user.userName)
    const dispatch = useDispatch()

    useEffect(() => {

        async function userCheck() {
            const userFetch = await fetch("http://localhost:3001/api/v1/user/profile",
                {
                    method: "GET",
                    headers: {
                        "Authorization": `Bearer ${token}`,
                        "Content-Type": "application/json",
                    },
                })

            const userData = await userFetch.json();
            console.log("Requete fonctionne")
            dispatch(setProfile(userData.body))
        }

        userCheck()
    }, [dispatch, token])



    return (
        <main className="main bg-dark">
            <div className="header">
                <h1>Welcome back<br />Tony Jarvis!</h1>
                <button className="edit-button" >Edit Name</button>
            </div>


            {/* <Form></Form> */}
            <div className="username-form">
                <h1>Edit user info</h1>
                <div className="username-input">
                    <label htmlFor="userName">User name</label>
                    <input type="password" id="password" />
                </div>
                <div className="username-input">
                    <label htmlFor="firstName">First name</label>
                    <input type="firstName" id="firstName" disabled="disabled" placeholder={`${firstName}`} />
                </div>
                <div className="username-input">
                    <label htmlFor="lastName">Last name</label>
                    <input type="lastName" id="lastName" disabled="disabled" placeholder={`${lastName}`} />
                </div>
                <div className="editors-button">
                    <button className="editor-button" >Save</button>
                    <button className="editor-button" >Cancel</button>
                </div>
            </div>
            {/* <Form></Form> */}


            <h2 className="sr-only">Accounts</h2>
            <TransactionSummary
                name={"Argent Bank Checking (x8349)"}
                amount={"$2,082.79"}
                balance={"Available Balance"}
            />
            <TransactionSummary
                name={"Argent Bank Savings (x6712)"}
                amount={"$10,928.42"}
                balance={"Available Balance"}
            />
            <TransactionSummary
                name={"Argent Bank Credit Card (x8349)"}
                amount={"$184.30"}
                balance={"Current Balance"}
            />
        </main>

    );
}
