import "../../assets/css/main.css";
import { useStore } from "react-redux";
import { useEffect, useState } from "react";
import { Navigate, useLocation, } from "react-router-dom";



export default function SignIn() {


    const store = useStore();
    const [userToken, setToken] = useState(store.getState().userToken);


    async function logInCheck(email, password) {
        const logInFetch = await fetch("http://localhost:3001/api/v1/user/login",
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ email, password }),
            }
        )
        const logInData = await logInFetch.json();
        if (logInData !== null) {
            setToken(logInData.body.token)
            console.log(logInData.body.token)

        }
        console.log(userToken)
        return (logInData.body.token)
    }




    // const handleSubmit = (evt) => {
    //     evt.preventDefault();
    //     const firstName = evt.currentTarget.firstName.value;
    //     store.dispatch({
    //         type: "UPDATE_FIRSTNAME",
    //         payload: firstName,
    //     });
    // };

    const handleLogIn = (evt) => {
        evt.preventDefault();
        const curUsername = evt.currentTarget.username.value;
        const curPassword = evt.currentTarget.password.value;
        const isConnected = logInCheck(curUsername, curPassword)
        if (isConnected !== null) {
            store.dispatch({
                type: "USER_TOKEN",
                payload: userToken,
            });

            return (<Navigate to="/user" replace={false} />)
        }

        console.log(" je suis un token " + userToken)
        console.log(curUsername)
        console.log(curPassword)
    }

    useEffect(() => {
        store.subscribe(() => setToken(store.getState().userToken));
    });

    return (
        <main className="main bg-dark">
            <section className="sign-in-content">
                <i className="fa fa-user-circle sign-in-icon"></i>
                <h1>Sign In</h1>
                <form onSubmit={handleLogIn}>
                    <div className="input-wrapper">
                        <label htmlFor="username">Username</label><input type="text" id="username" />
                    </div>
                    <div className="input-wrapper">
                        <label htmlFor="password">Password</label><input type="password" id="password" />
                    </div>
                    <div className="input-remember">
                        <input type="checkbox" id="remember-me" /><label htmlFor="remember-me"
                        >Remember me</label>
                    </div>
                    {/* <!-- PLACEHOLDER DUE TO STATIC SITE --> */}
                    {/* <a href="./user.html" class="sign-in-button">Sign In</a> */}
                    {/* <!-- SHOULD BE THE BUTTON BELOW -->
          <!-- <button class="sign-in-button">Sign In</button> -->
          <!--  --> */}
                    <button className="sign-in-button" type="submit">Sign In</button>
                </form>
            </section>
        </main>

    );
}
