import { useState } from "react";
import "../../assets/css/main.css"
import TransactionSummary from "../../components/transaction-summary/transaction-summary"
import { useStore } from "react-redux";

export default function User() {
    const store = useStore();
    const userToken = useState(store.getState().userToken)

    async function userCheck() {
        const userFetch = await fetch("http://localhost:3001/api/v1/user/profile",
            {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: userToken,
                },
            })
    }





    return (
        <main className="main bg-dark">
            <div className="header">
                <h1>Welcome back<br />Tony Jarvis!</h1>
                <button className="edit-button">Edit Name</button>
            </div>
            <h2 className="sr-only">Accounts</h2>
            <TransactionSummary
                name={"Argent Bank Checking (x8349)"}
                amount={"$2,082.79"}
                balance={"Available Balance"}
            />
            <TransactionSummary
                name={"Argent Bank Savings (x6712)"}
                amount={"$10,928.42"}
                balance={"Available Balance"}
            />
            <TransactionSummary
                name={"Argent Bank Credit Card (x8349)"}
                amount={"$184.30"}
                balance={"Current Balance"}
            />
        </main>

    );
}
