import "../../assets/css/main.css"
import logo from "../../assets/img/argentBankLogo.png";
import { useSelector } from "react-redux";
import { useEffect, } from "react";



export default function Header() {
    const userName = useSelector(state => state.user.userName)


    const handleLogOut = () => {
        setToken()
        console.log(userToken)
    }

    useEffect(() => {
        store.subscribe(() => setToken(store.getState().userToken));
    });


    return (
        <nav className="main-nav">
            <a className="main-nav-logo" href="/">
                <img className="main-nav-logo-image" src={logo} alt="Argent Bank Logo" />
                <h1 className="sr-only">Argent Bank</h1>
            </a>
            <div>
                {userToken === undefined ? <a className="main-nav-item" href="/sign-in">
                    <i className="fa fa-user-circle"></i>Sign In
                </a> : <a className="main-nav-item" href="/sign-in" onClick={handleLogOut}>
                    <i className="fa fa-user-circle"></i>Sign out
                </a>}
            </div>
        </nav>
    );
}
