import "../../assets/css/main.css"
import { useEffect, useState } from "react";
import { useStore } from "react-redux";

// export const Owner = () => {
//   const store = useStore();
// const [owner, setOwner] = useState(store.getState().owner);

const handleSubmit = (evt) => {
    evt.preventDefault();
    const firstName = evt.currentTarget.firstName.value;
    store.dispatch({
        type: "UPDATE_FIRSTNAME",
        payload: firstName,
    });
};

useEffect(() => {
    store.subscribe(() => setOwner(store.getState().owner));
});




export default function Home() {
    const store = useStore();
    const [username, setUsername] = useState(store.getState().username);

    async function logInCheck(email, password) {
        const logInFetch = await fetch("http://localhost:3001/api/v1/user/login",
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ email, password }),
            }
        )
        const logInData = await logInFetch.json();
        console.log(logInData)
        return (logInData)
    }




    // const handleSubmit = (evt) => {
    //     evt.preventDefault();
    //     const firstName = evt.currentTarget.firstName.value;
    //     store.dispatch({
    //         type: "UPDATE_FIRSTNAME",
    //         payload: firstName,
    //     });
    // };

    const handleLogIn = (evt) => {
        evt.preventDefault();
        const curUsername = evt.currentTarget.username.value;
        store.dispatch({
            type: "UPDATE_FIRSTNAME",
            payload: username,
        });
        const curPassword = evt.currentTarget.password.value;
        store.dispatch({
            type: "UPDATE_FIRSTNAME",
            payload: password,
        });
        const isConnected = logInCheck(curUsername, curPassword)
        console.log(isConnected)
        console.log(curUsername)
        console.log(curPassword)
    }

    return (
        <main class="main bg-dark">
            <section class="sign-in-content">
                <i class="fa fa-user-circle sign-in-icon"></i>
                <h1>Sign In</h1>
                <form onSubmit={handleLogIn}>
                    <div class="input-wrapper">
                        <label for="username">Username</label><input type="text" id="username" />
                    </div>
                    <div class="input-wrapper">
                        <label for="password">Password</label><input type="password" id="password" />
                    </div>
                    <div class="input-remember">
                        <input type="checkbox" id="remember-me" /><label for="remember-me"
                        >Remember me</label>
                    </div>
                    {/* <!-- PLACEHOLDER DUE TO STATIC SITE --> */}
                    {/* <a href="./user.html" class="sign-in-button">Sign In</a> */}
                    {/* <!-- SHOULD BE THE BUTTON BELOW -->
          <!-- <button class="sign-in-button">Sign In</button> -->
          <!--  --> */}
                    <button className="sign-in-button" type="submit">Sign In</button>
                </form>
            </section>
        </main>

    );
}
