import "../../assets/css/main.css"
import logo from "../../assets/img/argentBankLogo.png";
import { useSelector } from "react-redux";
import "../Header/Header.css"
import ReactDOM from 'react-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCircleUser } from '@fortawesome/free-solid-svg-icons'

const profileIcon = <FontAwesomeIcon icon={faCircleUser} />




export default function Header() {
    const userName = useSelector(state => state.user.userName)

    return (
        <nav className="main-nav">
            <a className="main-nav-logo" href="/">
                <img className="main-nav-logo-image" src={logo} alt="Argent Bank Logo" />
                <h1 className="sr-only">Argent Bank</h1>
            </a>
            <div>
                {!userName ? (<a className="main-nav-item" href="/sign-in"><i className="fa fa-user-circle"></i>Sign In</a>) : (<a className="main-nav-item" href="/sign-in" ><i className="fa fa-user-circle"></i>Sign out<p>{userName}</p></a>)}
            </div>
        </nav>
    );
}
